import{_ as a,c as r,ae as o,o as e}from"./chunks/framework.ilBJtLV6.js";const p=JSON.parse('{"title":"🚀 什么是 MiaoSpeed？","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"about/whatisthat.md","filePath":"about/whatisthat.md","lastUpdated":1755329374000}'),s={name:"about/whatisthat.md"};function l(n,t,i,h,d,g){return e(),r("div",null,t[0]||(t[0]=[o('<h1 id="🚀-什么是-miaospeed" tabindex="-1">🚀 什么是 MiaoSpeed？ <a class="header-anchor" href="#🚀-什么是-miaospeed" aria-label="Permalink to &quot;🚀 什么是 MiaoSpeed？&quot;">​</a></h1><p><strong>MiaoSpeed</strong> 是一款专为 <strong>测试代理节点</strong> 而设计的 <strong>后端测速服务</strong>。<br> 它支持 <strong>Socks5 / HTTP / Shadowsocks / VMess / VLESS / Trojan / Hysteria / Hysteria2 等主流协议</strong>，<br> 能够进行 <strong>延迟测试、带宽速度测试、流媒体解锁检测</strong>，帮助快速筛选最优节点，为用户提供更稳定流畅的体验。</p><p>MiaoSpeed 本身是一个 <strong>后端程序</strong>，需要配合前端面板或客户端调用使用。</p><h3 id="🔗-已支持的前端" tabindex="-1">🔗 已支持的前端 <a class="header-anchor" href="#🔗-已支持的前端" aria-label="Permalink to &quot;🔗 已支持的前端&quot;">​</a></h3><ul><li>🐱 <strong>MiaoKo</strong> —— MiaoKo的原生后端</li><li>🌀 <strong>Fulltclash</strong></li><li>🔑 <strong>Kopiy</strong></li></ul><hr><h2 id="🔑-核心能力" tabindex="-1">🔑 核心能力 <a class="header-anchor" href="#🔑-核心能力" aria-label="Permalink to &quot;🔑 核心能力&quot;">​</a></h2><ul><li><strong>协议全面</strong>：支持主流代理协议（socks5/http/ss等）</li><li><strong>测速维度</strong>：延迟 / 速度 / 流媒体解锁等</li><li><strong>高性能设计</strong>：内置线程池，支持大规模节点并发测速</li><li><strong>灵活部署</strong>：支持 Linux、OpenWrt、Docker、Systemd、手工部署等</li><li><strong>安全认证</strong>：Token 验证 / mTLS 支持，防止未授权调用</li><li><strong>多种配置方式</strong>：命令行参数 / 环境变量 / <code>.env</code> 文件</li></ul><hr><h2 id="🛠️-适用场景" tabindex="-1">🛠️ 适用场景 <a class="header-anchor" href="#🛠️-适用场景" aria-label="Permalink to &quot;🛠️ 适用场景&quot;">​</a></h2><ul><li><strong>机场节点测试</strong>：批量节点测速，实时选优线路</li><li><strong>前端系统对接</strong>：作为后端测速引擎，为前端提供 API</li><li><strong>个人用户</strong>：可通过 <strong>MiaoKo / Fulltclash / Kopiy 等前端</strong>调用进行节点测试</li></ul><hr><h2 id="📦-部署与使用" tabindex="-1">📦 部署与使用 <a class="header-anchor" href="#📦-部署与使用" aria-label="Permalink to &quot;📦 部署与使用&quot;">​</a></h2><ul><li><a href="https://www.miaospeed.com/install/docker.html" target="_blank" rel="noreferrer">Docker 一键部署</a></li><li><a href="https://www.miaospeed.com/install/manual.html" target="_blank" rel="noreferrer">Systemd / OpenWrt 手工部署</a></li><li><a href="https://www.miaospeed.com/install/envConf.html" target="_blank" rel="noreferrer">环境变量配置</a></li><li><a href="https://www.miaospeed.com/install/command.html" target="_blank" rel="noreferrer">命令行参数</a></li></ul><hr><h2 id="🔮-未来规划" tabindex="-1">🔮 未来规划 <a class="header-anchor" href="#🔮-未来规划" aria-label="Permalink to &quot;🔮 未来规划&quot;">​</a></h2><ul><li><strong>API 扩展</strong>：持续优化与扩展，支持更多前端系统调用</li><li><strong>签名机制优化</strong>：增强安全性与调用验证，确保测速请求可信可靠</li></ul><hr><p>✨ <strong>一句话总结</strong>：<br> MiaoSpeed = 面向代理节点的 <strong>全协议后端测速引擎</strong>，测试更快、更准、更全面。</p>',19)]))}const m=a(s,[["render",l]]);export{p as __pageData,m as default};
