import{_ as a,c as i,ae as t,o as r}from"./chunks/framework.ilBJtLV6.js";const g=JSON.parse('{"title":"环境要求","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"install/ENV.md","filePath":"install/ENV.md","lastUpdated":1755327740000}'),o={name:"install/ENV.md"};function e(n,l,s,d,u,h){return r(),i("div",null,l[0]||(l[0]=[t('<h1 id="环境要求" tabindex="-1">环境要求 <a class="header-anchor" href="#环境要求" aria-label="Permalink to &quot;环境要求&quot;">​</a></h1><h2 id="_1-架构支持" tabindex="-1">1. 架构支持 <a class="header-anchor" href="#_1-架构支持" aria-label="Permalink to &quot;1. 架构支持&quot;">​</a></h2><p>支持以下常见的处理器架构：</p><ul><li><strong>x86_64</strong>（64 位）</li><li><strong>ARM64</strong>（64 位，例如 Raspberry Pi 和部分云服务器）</li><li><strong>ARMv7</strong>（32 位，例如旧版 Raspberry Pi）</li></ul><h2 id="_2-最低硬件配置" tabindex="-1">2. 最低硬件配置 <a class="header-anchor" href="#_2-最低硬件配置" aria-label="Permalink to &quot;2. 最低硬件配置&quot;">​</a></h2><ul><li><strong>CPU</strong>：1 核心</li><li><strong>内存</strong>：512 MB</li><li><strong>硬盘</strong>：10 GB 可用空间</li></ul><h2 id="_3-操作系统支持" tabindex="-1">3. 操作系统支持 <a class="header-anchor" href="#_3-操作系统支持" aria-label="Permalink to &quot;3. 操作系统支持&quot;">​</a></h2><ul><li><strong>Linux</strong>（推荐，支持以下发行版）： <ul><li>Ubuntu 18.04 或更高版本</li><li>Debian 9 或更高版本</li><li>CentOS 7 或更高版本</li><li>Alpine Linux 3.14 或更高版本</li></ul></li><li><strong>Windows</strong>： <ul><li>Windows 10 64 位或更高版本</li></ul></li><li><strong>macOS</strong>： <ul><li>macOS 10.15 (Catalina) 或更高版本</li></ul></li></ul><h2 id="_4-网络要求" tabindex="-1">4. 网络要求 <a class="header-anchor" href="#_4-网络要求" aria-label="Permalink to &quot;4. 网络要求&quot;">​</a></h2><ul><li><strong>最低带宽</strong>：1 Mbps</li><li><strong>网络访问</strong>：需要访问公网以下载所需依赖。</li></ul><h2 id="_5-推荐配置-非必需" tabindex="-1">5. 推荐配置（非必需） <a class="header-anchor" href="#_5-推荐配置-非必需" aria-label="Permalink to &quot;5. 推荐配置（非必需）&quot;">​</a></h2><p>为获得更佳性能，推荐使用以下配置：</p><ul><li><strong>CPU</strong>：2 核心或以上</li><li><strong>内存</strong>：2 GB 或更高</li><li><strong>硬盘</strong>：20 GB 或更高</li><li><strong>网络带宽</strong>：10 Mbps 或更高</li></ul><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><ol><li>确保目标机器安装了基础工具（如 <code>curl</code>、<code>wget</code>）。</li><li>对于云服务器，可以选择最低配实例（如 AWS t2.micro、阿里云轻量应用服务器等）。</li></ol>',15)]))}const _=a(o,[["render",e]]);export{g as __pageData,_ as default};
